<script type="text/javascript">


function postResult(){
var msg=document.getElementById("msg").value;
var xhr = new XMLHttpRequest();
var json = JSON.stringify({
  msg: document.getElementById("msg").value
});
xhr.open("POST", '/<?php echo basename($_SERVER['SCRIPT_NAME']);?>', true)
xhr.setRequestHeader('Content-type', 'application/json; charset=utf-8');
xhr.onreadystatechange = function () {
        if (xhr.readyState == 4) {
            if (xhr.status == 200) {
                var data = xhr.responseText;
			//	alert(data);
		if(data=="false"){
        $('#error_message').html("Сообщение не отправилось!");
        $("#error_box").fadeIn(500).delay(3000).fadeOut(500);
		}else if(data=="true"){
		$('#success_message').html("Сообщение отправленно");
        $("#success_box").fadeIn(500).delay(3000).fadeOut(500);
		document.getElementById('msg').value = "";
		}
            }
        }
    };
xhr.send(json);
}

</script>
